  }
  for (let i = 0; i < seekable.length; i++) {
    if (seekable.start(i) <= time && seekable.end(i) >= time) {
      return true;
    }
  }
  return false;
}

export const toPlaybackIdParts = (playbackIdWithOptionalParams: string): [string, string?] => {
  const qIndex = playbackIdWithOptionalParams.indexOf('?');
  if (qIndex < 0) return [playbackIdWithOptionalParams];
  const idPart = playbackIdWithOptionalParams.slice(0, qIndex);
  const queryPart = playbackIdWithOptionalParams.slice(qIndex);
  return [idPart, queryPart];
};

export const getType = (props: Partial<Pick<MuxMediaProps, 'type' | 'src' | 'customDomain'>>) => {
  const { type } = props;
  if (type) {
    const upperType = type.toUpperCase();
    return isKeyOf(upperType, MimeTypeShorthandMap) ? MimeTypeShorthandMap[upperType] : type;
  }
  return inferMimeTypeFromURL(props);
};

export const toStreamTypeFromPlaylistType = (playlistType: HlsPlaylistTypes) => {
  return playlistType === 'VOD' ? StreamTypes.ON_DEMAND : StreamTypes.LIVE;
};

export const toTargetLiveWindowFromPlaylistType = (playlistType: HlsPlaylistTypes) => {
  if (playlistType === 'EVENT') return Number.POSITIVE_INFINITY;
  if (playlistType === 'VOD') return Number.NaN;
  return 0;
};

export const inferMimeTypeFromURL = (props: Partial<Pick<MuxMediaProps, 'src' | 'customDomain'>>) => {
  const { src } = props;
  if (!src) return '';

  let pathname = '';
  try {
    pathname = new URL(src).pathname;
  } catch (_e) {
    console.error('invalid url');
  }

  const extDelimIdx = pathname.lastIndexOf('.');
  if (extDelimIdx < 0) {
    if (isExtensionLessMuxM3U8URL(props)) {
      return ExtensionMimeTypeMap.M3U8; // Treat extension-less Mux URLs as HLS
    }
    return '';
  }

  const ext = pathname.slice(extDelimIdx + 1);
  const upperExt = ext.toUpperCase();

  return isKeyOf(upperExt, ExtensionMimeTypeMap) ? ExtensionMimeTypeMap[upperExt] : '';
};

const MUX_VIDEO_DOMAIN = 'mux.com';
export const isExtensionLessMuxM3U8URL = ({
  src,
  customDomain = MUX_VIDEO_DOMAIN,
}: Partial<Pick<MuxMediaProps, 'src' | 'customDomain'>>) => {
  let urlObj;
  try {
    urlObj = new URL(`${src}`);
  } catch {
    return false;
  }
  const validProtocol = urlObj.protocol === 'https:';
  const validHostname = urlObj.hostname === `stream.${customDomain}`.toLowerCase();
  const pathParts = urlObj.pathname.split('/');
  const validPathPartsLength = pathParts.length === 2;
  const validExtensionlessPath = !pathParts?.[1].includes('.');
  return validProtocol && validHostname && validPathPartsLength && validExtensionlessPath;
};

export type MuxJWT = {
  sub: string;
  aud: 'v' | 't' | 'g' | 's' | 'd';
  exp: number;
};

export const parseJwt = (token: string | undefined): Partial<MuxJWT> | undefined => {
  const base64Url = (token ?? '').split('.')[1];

  // exit early on invalid value
  if (!base64Url) return undefined;

  // Account for malformed JWTs
  try {
    const base64 = base64Url.replace(/-/g, '+').replace(/_/g, '/');
    const jsonPayload = decodeURIComponent(
      atob(base64)
        .split('')
        .map(function (c) {
          return '%' + ('00' + c.charCodeAt(0).toString(16)).slice(-2);
        })
        .join('')
    );
    return JSON.parse(jsonPayload);
  } catch {
    return undefined;
  }
};

export const isJWTExpired = ({ exp }: Partial<Pick<MuxJWT, 'exp'>>, referenceTime: number = Date.now()) => {
  return !exp || exp * 1000 < referenceTime;
};

// NOTE: Treating missing sub (and expected sub) as mismatches for now (CJP)
export const isJWTSubMismatch = ({ sub }: Partial<Pick<MuxJWT, 'sub'>>, expectedSub: string | undefined) => {
  return sub !== expectedSub;
};

export const isJWTAudMissing = ({ aud }: Partial<Pick<MuxJWT, 'aud'>>, _expectedAud: string | undefined) => {
  return !aud;
};

export const isJWTAudMismatch = ({ aud }: Partial<Pick<MuxJWT, 'aud'>>, expectedAud: string | undefined) => {
  return aud !== expectedAud;
};

const DEFAULT_LOCALE = 'en';

// NL example
// lang = {
//   "Network Error": "Netwerk Fout",
// };
export function i18n(str: string, translate = true) {
  const message = translate ? ((lang as unknown as any)?.[str] ?? str) : str;
  const locale = translate ? (lang as unknown as any).code : DEFAULT_LOCALE;
  return new IntlMessageFormat(message, locale);
}

/**
 * Poor man's IntlMessageFormat, enrich if need be.
 * @see https://formatjs.io/docs/intl-messageformat/
 */
class IntlMessageFormat {
  message: string;
  locale: string;

  /** @TODO re-implement esbuild custom plugin for code usage (CJP) */
  constructor(message: string, locale = (lang as unknown as any) ?? DEFAULT_LOCALE) {
    this.message = message;
    this.locale = locale;
  }

  format(values: Record<string, any>): string {
    return this.message.replace(/\{(\w+)\}/g, (_match, key) => {
      return values[key] ?? '';
    });
  }

  toString() {
    return this.message;
  }
}
import { addEventListenerWithTeardown } from './util';
import { ValueOf, Autoplay, AutoplayTypes, PlaybackEngine, MuxMediaPropsInternal, StreamTypes } from './types';
import Hls from './hls';

const AutoplayTypeValues = Object.values(AutoplayTypes);
export const isAutoplayValue = (value: unknown): value is Autoplay => {
  return (
    typeof value === 'boolean' ||
    (typeof value === 'string' && AutoplayTypeValues.includes(value as ValueOf<AutoplayTypes>))
  );
};

// Given a video element, will listen to lifecycle events to determine important
// things like whether the video is live or whether the video has played
// and then handles autoplaying the video as appropraite.
// It works with both the native video element or hls.js.
// This returns a method UpdateAutoplay, that allows the user to change
// the value of the autoplay attribute and it will react appropriately.
export const setupAutoplay = (
  props: Partial<MuxMediaPropsInternal>,
  mediaEl: HTMLMediaElement,
  hls?: PlaybackEngine
) => {
  const { autoplay: maybeAutoplay } = props;
  let hasPlayed = false;
  let isLive = false;
  let autoplay: Autoplay = isAutoplayValue(maybeAutoplay) ? maybeAutoplay : !!maybeAutoplay;

  const updateHasPlayed = () => {
    // hasPlayed

    if (!hasPlayed) {
      addEventListenerWithTeardown(
        mediaEl,
        'playing',
        () => {
          hasPlayed = true;
        },
        { once: true }
      );
    }
  };

  updateHasPlayed();

  // on `loadstart`
  // hasPlayed should default to false
  // we should try and autoplay
  addEventListenerWithTeardown(
    mediaEl,
    'loadstart',
    () => {
      hasPlayed = false;
      updateHasPlayed();
      handleAutoplay(mediaEl, autoplay);
    },
    { once: true }
  );

  // on `loadedmetadata` we can check whether we're live in the case of native playback
  addEventListenerWithTeardown(
    mediaEl,
    'loadstart',
    () => {
      // only update isLive here if we're using native playback
      if (!hls) {
        // defer to streamType if set (including inferred)
        if (props.streamType && props.streamType !== StreamTypes.UNKNOWN) {
          isLive = props.streamType === StreamTypes.LIVE;
        } else {
          isLive = !Number.isFinite(mediaEl.duration);
        }
      }
      handleAutoplay(mediaEl, autoplay);
    },
    { once: true }
  );

  // determine if we're live for hls.js
  if (hls) {
    hls.once(Hls.Events.LEVEL_LOADED, (_event: any, data: any) => {
      // defer to streamType if set (including inferred)
      if (props.streamType && props.streamType !== StreamTypes.UNKNOWN) {
        isLive = props.streamType === StreamTypes.LIVE;
      } else {
        isLive = data.details.live ?? false;
      }
    });
  }

  // When we are not auto-playing, we should seek to the live sync position
  // This will seek first play event of *any* live video including event-type,
  // which probably shouldn't seek
  if (!autoplay) {
    const handleSeek = () => {
      // don't seek if we're not live or if a `startTime` has been explicitly set
      if (!isLive || Number.isFinite(props.startTime)) {
        return;
      }
      // seek to either hls.js's liveSyncPosition or the native seekable end
      if (hls?.liveSyncPosition) {
        mediaEl.currentTime = hls.liveSyncPosition;
      } else {
        if (Number.isFinite(mediaEl.seekable.end(0))) {
          mediaEl.currentTime = mediaEl.seekable.end(0);
        }
      }
    };
    if (hls) {
      addEventListenerWithTeardown(
        mediaEl,
        'play',
        () => {
          if (mediaEl.preload === 'metadata') {
            hls.once(Hls.Events.LEVEL_UPDATED, handleSeek);
          } else {
            handleSeek();
          }
        },
        { once: true }
      );
    }
  }

  // this method allows us to update the value of autoplay
  // and try autoplaying appropriately.
  const updateAutoplay = (newAutoplay?: Autoplay) => {
    if (!hasPlayed) {
      autoplay = isAutoplayValue(newAutoplay) ? newAutoplay : !!newAutoplay;
      handleAutoplay(mediaEl, autoplay);
    }
  };

  return updateAutoplay;
};

export const handleAutoplay = (mediaEl: HTMLMediaElement, autoplay: Autoplay) => {
  if (!autoplay) {
    return;
  }

  const oldMuted = mediaEl.muted;
  const restoreMuted = () => (mediaEl.muted = oldMuted);

  switch (autoplay) {
    // ANY:
    // try to play with current options
    // if it fails, mute and try playing again
    // if that fails, restore muted state and don't try playing again
    case AutoplayTypes.ANY:
      mediaEl.play().catch(() => {
        mediaEl.muted = true;
        mediaEl.play().catch(restoreMuted);
      });
      break;

    // MUTED:
    // mute the player and then try playing
    // if that fails, restore muted state
    case AutoplayTypes.MUTED:
      mediaEl.muted = true;
      mediaEl.play().catch(restoreMuted);
      break;

    // Default or if autoplay is a boolean attribute:
    // Try playing the video and catch the failed autoplay warning
    default: // eslint-disable-next-line
      mediaEl.play().catch(() => {});
      break;
  }
};
import { addEventListenerWithTeardown } from './util';
import { PlaybackEngine } from './types';

export const setupPreload = (
  { preload, src }: Partial<HTMLMediaElement>,
  mediaEl: HTMLMediaElement,
  hls?: PlaybackEngine
) => {
  const updatePreload = (val?: HTMLMediaElement['preload']) => {
    if (val != null && ['', 'none', 'metadata', 'auto'].includes(val)) {
      mediaEl.setAttribute('preload', val);
    } else {
      mediaEl.removeAttribute('preload');
    }
  };

  // handle native without hls.js (MSE)
  if (!hls) {
    updatePreload(preload);
    return updatePreload;
  }

  let hasLoadedSource = false;
  let hasPlayFired = false;

  const originalLength = hls.config.maxBufferLength;
  const originalSize = hls.config.maxBufferSize;

  const updateHlsPreload = (val?: HTMLMediaElement['preload']) => {
    // even if it doesn't have an effect on a <video> w/ MSE
    // still update the `preload` attribute.
    updatePreload(val);

    const newPreload = val ?? mediaEl.preload;
    if (hasPlayFired || newPreload === 'none') return;
    if (newPreload === 'metadata') {
      // load the least amount of data possible
      hls.config.maxBufferLength = 1;
      hls.config.maxBufferSize = 1;
    } else {
      hls.config.maxBufferLength = originalLength;
      hls.config.maxBufferSize = originalSize;
    }

    safeLoadSource();
  };

  const safeLoadSource = () => {
    if (!hasLoadedSource && src) {
      hasLoadedSource = true;
      hls.loadSource(src);
    }
  };

  addEventListenerWithTeardown(
    mediaEl,
    'play',
    () => {
      hasPlayFired = true;

      // once a user has played, allow for it to load data as normal
      hls.config.maxBufferLength = originalLength;
      hls.config.maxBufferSize = originalSize;

      // load the source on first play if needed
      safeLoadSource();
    },
    { once: true }
  );

  updateHlsPreload(preload);

  return updateHlsPreload;
};
import Hls from './hls';

export function setupMediaTracks(
  customMediaEl: HTMLMediaElement,
  hls: Pick<
    Hls,
    'audioTrack' | 'audioTracks' | 'autoLevelEnabled' | 'nextLevel' | 'levels' | 'on' | 'once' | 'off' | 'trigger'
  >
) {
  if (!('videoTracks' in customMediaEl)) return;

  // Create a map to save the unique id's we create for each level and rendition.
  // hls.js uses the levels array index primarily but we'll use the id to have a
  // 1 to 1 relation from rendition to level.
  const levelIdMap = new WeakMap();

  hls.on(Hls.Events.MANIFEST_PARSED, function (_event, data) {
    removeAllMediaTracks();

    const videoTrack = customMediaEl.addVideoTrack('main');
    videoTrack.selected = true;

    for (const [id, level] of data.levels.entries()) {
      const videoRendition = videoTrack.addRendition(
        level.url[0],
        level.width,
        level.height,
        level.videoCodec,
        level.bitrate
      );

      // The returned levels all have an id of `0`, save the id in a WeakMap.
      levelIdMap.set(level, `${id}`);
      videoRendition.id = `${id}`;
    }
  });

  hls.on(Hls.Events.AUDIO_TRACKS_UPDATED, function (_event, data) {
    removeAudioTracks();

    for (const a of data.audioTracks) {
      // hls.js doesn't return a `kind` property for audio tracks yet.
      const kind = a.default ? 'main' : 'alternative';
      const audioTrack = customMediaEl.addAudioTrack(kind, a.name, a.lang);
      audioTrack.id = `${a.id}`;

      if (a.default) {
        audioTrack.enabled = true;
      }
    }
  });

  customMediaEl.audioTracks.addEventListener('change', () => {
    // Cast to number, hls.js uses numeric id's.
    const audioTrackId = +[...customMediaEl.audioTracks].find((t) => t.enabled)?.id;
    const availableIds = hls.audioTracks.map((t) => t.id);
    if (audioTrackId != hls.audioTrack && availableIds.includes(audioTrackId)) {
      hls.audioTrack = audioTrackId;
    }
  });

  // Fired when a level is removed after calling `removeLevel()`
  hls.on(Hls.Events.LEVELS_UPDATED, function (_event, data) {
    const videoTrack = customMediaEl.videoTracks[customMediaEl.videoTracks.selectedIndex ?? 0];
    if (!videoTrack) return;

    const levelIds: string[] = data.levels.map((l) => levelIdMap.get(l));

    for (const rendition of customMediaEl.videoRenditions) {
      if (rendition.id && !levelIds.includes(rendition.id)) {
        videoTrack.removeRendition(rendition);
      }
    }
  });

  // hls.js doesn't support enabling multiple renditions.
  //
  // 1. if all renditions are enabled it's auto selection.
  // 2. if 1 of the renditions is disabled we assume a selection was made
  //    and lock it to the first rendition that is enabled.
  const switchRendition = (event: Event) => {
    // @ts-ignore
    const level = event.target.selectedIndex as number;
    if (level != hls.nextLevel) {
      hls.nextLevel = level;
    }
  };

  customMediaEl.videoRenditions?.addEventListener('change', switchRendition);

  const removeVideoTracks = () => {
    for (const videoTrack of customMediaEl.videoTracks) {
      customMediaEl.removeVideoTrack(videoTrack);
    }
  };

  const removeAudioTracks = () => {
    for (const audioTrack of customMediaEl.audioTracks) {
      customMediaEl.removeAudioTrack(audioTrack);
    }
  };

  const removeAllMediaTracks = () => {
    removeVideoTracks();
    removeAudioTracks();
  };

  // NOTE: Since this is only relevant for hls, using destroying event (CJP).
  hls.once(Hls.Events.DESTROYING, removeAllMediaTracks);
}
import Hls from './hls';
import { CuePoint, Chapter } from './types';
import { addEventListenerWithTeardown } from './util';

type Config = { label: string };

// Shared utils

// Extracts the start time from a cuepoint, considering legacy "time" prop
const cuePointStart = (cuePoint: CuePoint): number => {
  if ('time' in cuePoint) {
    return cuePoint.time;
  }
  return cuePoint.startTime;
};

export function setupTextTracks(
  mediaEl: HTMLMediaElement,
  hls: Pick<Hls, 'on' | 'once' | 'subtitleTracks' | 'subtitleTrack'>
) {
  hls.on(Hls.Events.NON_NATIVE_TEXT_TRACKS_FOUND, (_type, { tracks }) => {
    tracks.forEach((trackObj) => {
      const baseTrackObj = trackObj.subtitleTrack ?? trackObj.closedCaptions;
      const idx = hls.subtitleTracks.findIndex(({ lang, name, type }) => {
        return lang == baseTrackObj?.lang && name === trackObj.label && type.toLowerCase() === trackObj.kind;
      });

      // NOTE: Undocumented method for determining identifier by hls.js. Relied on for
      // ensuring CUES_PARSED events can identify and apply cues to the appropriate track (CJP).
      // See: https://github.com/video-dev/hls.js/blob/master/src/controller/timeline-controller.ts#L640
      const id = (trackObj._id ?? trackObj.default) ? 'default' : `${trackObj.kind}${idx}`;

      addTextTrack(mediaEl, trackObj.kind as TextTrackKind, trackObj.label, baseTrackObj?.lang, id, trackObj.default);
    });
  });

  const changeHandler = () => {
    if (!hls.subtitleTracks.length) return;

    const showingTrack = Array.from(mediaEl.textTracks).find((textTrack) => {
      return textTrack.id && textTrack.mode === 'showing' && ['subtitles', 'captions'].includes(textTrack.kind);
    });

    if (!showingTrack) return;

    const currentHlsTrack = hls.subtitleTracks[hls.subtitleTrack];

    // If hls.subtitleTrack is -1 or its id changed compared to the one that is showing load the new subtitle track.
    const hlsTrackId = !currentHlsTrack
      ? undefined
      : currentHlsTrack.default
        ? 'default'
        : `${hls.subtitleTracks[hls.subtitleTrack].type.toLowerCase()}${hls.subtitleTrack}`;

    if (hls.subtitleTrack < 0 || showingTrack?.id !== hlsTrackId) {
      const idx = hls.subtitleTracks.findIndex(({ lang, name, type, default: defaultTrack }) => {
        return (
          (showingTrack.id === 'default' && defaultTrack) ||
          (lang == showingTrack.language && name === showingTrack.label && type.toLowerCase() === showingTrack.kind)
        );
      });
      // After the subtitleTrack is set here, hls.js will load the playlist and CUES_PARSED events will be fired below.
      hls.subtitleTrack = idx;
    }

    if (showingTrack?.id === hlsTrackId) {
      // Refresh the cues after a texttrack mode change to fix a Chrome bug causing the captions not to render.
      if (showingTrack.cues) {
        Array.from(showingTrack.cues).forEach((cue) => {
          showingTrack.addCue(cue);
        });
      }
    }
  };

  mediaEl.textTracks.addEventListener('change', changeHandler);

  hls.on(Hls.Events.CUES_PARSED, (_type, { track, cues }) => {
    const textTrack = mediaEl.textTracks.getTrackById(track);
    if (!textTrack) return;

    const disabled = textTrack.mode === 'disabled';
    if (disabled) {
      textTrack.mode = 'hidden';
    }

    cues.forEach((cue: VTTCue) => {
      if (textTrack.cues?.getCueById(cue.id)) return;
      textTrack.addCue(cue);
    });

    if (disabled) {
      textTrack.mode = 'disabled';
    }
  });

  // NOTE: Since this is only relevant for hls, using destroying event (CJP).
  hls.once(Hls.Events.DESTROYING, () => {
    mediaEl.textTracks.removeEventListener('change', changeHandler);
    // Use data attribute to identify tracks that should be removed when switching sources/destroying hls.js instance.
    const trackEls: NodeListOf<HTMLTrackElement> = mediaEl.querySelectorAll('track[data-removeondestroy]');
    trackEls.forEach((trackEl) => {
      trackEl.remove();
    });
  });

  const forceHiddenTracks = () => {
    // Keeping this a forEach in case we want to expand the scope of this.
    Array.from(mediaEl.textTracks).forEach((track) => {
      if (['subtitles', 'caption'].includes(track.kind)) return;
      if (!(track.label === 'thumbnails' || track.kind === 'chapters')) return;
      if (!track.cues?.length) {
        let selector = 'track';
        if (track.kind) selector += `[kind="${track.kind}"]`;
        if (track.label) selector += `[label="${track.label}"]`;
        const trackEl = mediaEl.querySelector(selector);
        // Force a reload of the cues if they've been removed
        const src = trackEl?.getAttribute('src') ?? '';
        trackEl?.removeAttribute('src');
        setTimeout(() => {
          trackEl?.setAttribute('src', src);
        }, 0);
      }
      // Force hidden mode if it's not hidden
      if (track.mode !== 'hidden') {
        track.mode = 'hidden';
      }
    });
  };

  // hls.js will forcibly clear all cues from tracks on manifest loads or media attaches.
  // This ensures that we re-load them after it's done that.
  hls.once(Hls.Events.MANIFEST_LOADED, forceHiddenTracks);
  hls.once(Hls.Events.MEDIA_ATTACHED, forceHiddenTracks);
}

export function addTextTrack(
  mediaEl: HTMLMediaElement,
  kind: TextTrackKind,
  label: string,
  lang?: string,
  id?: string,
  defaultTrack?: boolean
): TextTrack {
  const trackEl = document.createElement('track');
  trackEl.kind = kind;
  trackEl.label = label;
  if (lang) {
    // This attribute must be present if the element's kind attribute is in the subtitles state.
    trackEl.srclang = lang;
  }
  if (id) {
    trackEl.id = id;
  }
  if (!!defaultTrack) {
    trackEl.default = true;
  }
  trackEl.track.mode = ['subtitles', 'captions'].includes(kind) ? 'disabled' : 'hidden';

  // Add data attribute to identify tracks that should be removed when switching sources/destroying hls.js instance.
  trackEl.setAttribute('data-removeondestroy', '');
  mediaEl.append(trackEl);

  return trackEl.track as TextTrack;
}

export function removeTextTrack(mediaEl: HTMLMediaElement, track: TextTrack) {
  const trackElement: HTMLTrackElement | undefined = Array.prototype.find.call(
    mediaEl.querySelectorAll('track'),
    (trackEl: HTMLTrackElement) => trackEl.track === track
  );
  trackElement?.remove();
}

export function getTextTrack(mediaEl: HTMLMediaElement, label: string, kind: TextTrackKind) {
  return Array.from(mediaEl.querySelectorAll('track')).find((trackEl) => {
    return trackEl.track.label === label && trackEl.track.kind === kind;
  })?.track;
}

export async function addCuesToTextTrack<T = any>(
  mediaEl: HTMLMediaElement,
  cues: CuePoint<T>[] | Chapter[],
  label: string,
  kind: TextTrackKind
) {
  // If the track has already been created/added, use it.
  let track = getTextTrack(mediaEl, label, kind);
  if (!track) {
    // Otherwise, create a new one
    track = addTextTrack(mediaEl, kind, label);
    track.mode = 'hidden';
    // Wait a tick before providing a newly created track. Otherwise e.g. cues disappear when using track.addCue().
    await new Promise((resolve) => setTimeout(() => resolve(undefined), 0));
  }

  if (track.mode !== 'hidden') {
    track.mode = 'hidden';
  }

  // Copy cuePoints to ensure sort is not mutative
  [...cues]
    // Sort descending to ensure last cuepoints are added as cues first. This is done
    // so the track's cue's can be used for reference when determining an appropriate
    // endTime, allowing support of multiple invocations of addCuePoints
    .sort((cuePointA, cuePointB) => cuePointStart(cuePointB) - cuePointStart(cuePointA))
    .forEach((cuePoint) => {
      const value = cuePoint.value;
      const startTime = cuePointStart(cuePoint);

      if ('endTime' in cuePoint && cuePoint.endTime != undefined) {
        track?.addCue(
          new VTTCue(
            startTime,
            cuePoint.endTime,
            kind === 'chapters' ? (value as string) : JSON.stringify(value ?? null)
          )
        );
      } else {
        // find the cue that starts immediately after the cuePoint's time
        const cueAfterIndex = Array.prototype.findIndex.call(track?.cues, (cue) => cue.startTime >= startTime);
        const cueAfter = track?.cues?.[cueAfterIndex];
        const endTime = cueAfter
          ? cueAfter.startTime
          : Number.isFinite(mediaEl.duration)
            ? mediaEl.duration
            : Number.MAX_SAFE_INTEGER;

        // Adjust the endTime of the already added previous cue,
        // if present, so it does not overlap with the newly added cue.
        const previousCue = track?.cues?.[cueAfterIndex - 1];
        if (previousCue) {
          previousCue.endTime = startTime;
        }
        track?.addCue(
          new VTTCue(startTime, endTime, kind === 'chapters' ? (value as string) : JSON.stringify(value ?? null))
        );
      }
    });

  // NOTE: this doesn't naturally fire when we update the list
  // of cue points (without changing the active cue). We manually
  // fire this to force the state manager to reflect the new change
  mediaEl.textTracks.dispatchEvent(
    new Event('change', {
      bubbles: true,
      composed: true,
    })
  );

  return track;
}

// Cuepoints

const DEFAULT_CUEPOINTS_TRACK_LABEL = 'cuepoints';
export const DefaultCuePointsConfig: Config = Object.freeze({ label: DEFAULT_CUEPOINTS_TRACK_LABEL });

export async function addCuePoints<T>(
  mediaEl: HTMLMediaElement,
  cuePoints: CuePoint<T>[],
  cuePointsConfig: Config = DefaultCuePointsConfig
) {
  return addCuesToTextTrack(mediaEl, cuePoints, cuePointsConfig.label, 'metadata');
}

const toCuePoint = (cue: VTTCue) => ({
  time: cue.startTime,
  value: JSON.parse(cue.text),
});

export function getCuePoints(
  mediaEl: HTMLMediaElement,
  cuePointsConfig: Config = { label: DEFAULT_CUEPOINTS_TRACK_LABEL }
) {
  const track = getTextTrack(mediaEl, cuePointsConfig.label, 'metadata');
  if (!track?.cues) return [];
  return Array.from(track.cues, (cue) => toCuePoint(cue as VTTCue));
}

export function getActiveCuePoint(
  mediaEl: HTMLMediaElement,
  cuePointsConfig: Config = { label: DEFAULT_CUEPOINTS_TRACK_LABEL }
) {
  const track = getTextTrack(mediaEl, cuePointsConfig.label, 'metadata');
  if (!track?.activeCues?.length) return undefined;
  if (track.activeCues.length === 1) return toCuePoint(track.activeCues[0] as VTTCue);
  // NOTE: There is a bug in Chromium where there may be "lingering activeCues" even
  // after the playhead is no longer within their [startTime, endTime) bounds. This
  // accounts for those cases (CJP)
  const { currentTime } = mediaEl;
  const actualActiveCue = Array.prototype.find.call(track.activeCues ?? [], ({ startTime, endTime }) => {
    return startTime <= currentTime && endTime > currentTime;
  }) as VTTCue | undefined;
  if (!actualActiveCue) {
    return toCuePoint(track.activeCues[0] as VTTCue);
  }
  return toCuePoint(actualActiveCue);
}

export async function setupCuePoints(mediaEl: HTMLMediaElement, cuePointsConfig: Config = DefaultCuePointsConfig) {
  return new Promise((resolve) => {
    addEventListenerWithTeardown(mediaEl, 'loadstart', async () => {
      const track = await addCuePoints(mediaEl, [], cuePointsConfig);
      addEventListenerWithTeardown(
        mediaEl,
        'cuechange',
        () => {
          const activeCuePoint = getActiveCuePoint(mediaEl);
          if (activeCuePoint) {
            const evt = new CustomEvent('cuepointchange', {
              composed: true,
              bubbles: true,
              detail: activeCuePoint,
            });
            mediaEl.dispatchEvent(evt);
          }
        },
        {},
        track
      );
      resolve(track);
    });
  });
}

/**
 * Chapters
 */

const DEFAULT_CHAPTERS_TRACK_LABEL = 'chapters';
export const DefaultChaptersConfig: Config = Object.freeze({ label: DEFAULT_CHAPTERS_TRACK_LABEL });

const vttCueToChapter = (cue: VTTCue) => ({
  startTime: cue.startTime,
  endTime: cue.endTime,
  value: cue.text,
});

export async function addChapters(
  mediaEl: HTMLMediaElement,
  chapters: Chapter[],
  chaptersConfig: Config = DefaultChaptersConfig
) {
  return addCuesToTextTrack(mediaEl, chapters, chaptersConfig.label, 'chapters');
}

export function getChapters(
  mediaEl: HTMLMediaElement,
  chaptersConfig: Config = { label: DEFAULT_CHAPTERS_TRACK_LABEL }
) {
  const track = getTextTrack(mediaEl, chaptersConfig.label, 'chapters');
  if (!track?.cues?.length) return [];
  return Array.from(track.cues, (cue) => vttCueToChapter(cue as VTTCue));
}

export function getActiveChapter(
  mediaEl: HTMLMediaElement,
  chaptersConfig: Config = { label: DEFAULT_CHAPTERS_TRACK_LABEL }
) {
  const track = getTextTrack(mediaEl, chaptersConfig.label, 'chapters');
  if (!track?.activeCues?.length) return undefined;
  if (track.activeCues.length === 1) return vttCueToChapter(track.activeCues[0] as VTTCue);
  // NOTE: There is a bug in Chromium where there may be "lingering activeCues" even
  // after the playhead is no longer within their [startTime, endTime) bounds. This
  // accounts for those cases (CJP)
  const { currentTime } = mediaEl;
  const actualActiveCue = Array.prototype.find.call(track.activeCues ?? [], ({ startTime, endTime }) => {
    return startTime <= currentTime && endTime > currentTime;
  }) as VTTCue | undefined;
  if (!actualActiveCue) {
    return vttCueToChapter(track.activeCues[0] as VTTCue);
  }
  return vttCueToChapter(actualActiveCue);
}

export async function setupChapters(mediaEl: HTMLMediaElement, chaptersConfig: Config = DefaultChaptersConfig) {
  return new Promise((resolve) => {
    addEventListenerWithTeardown(mediaEl, 'loadstart', async () => {
      const track = await addChapters(mediaEl, [], chaptersConfig);

      addEventListenerWithTeardown(
        mediaEl,
        'cuechange',
        () => {
          const activeCuePoint = getActiveChapter(mediaEl);
          if (activeCuePoint) {
            const evt = new CustomEvent('chapterchange', {
              composed: true,
              bubbles: true,
              detail: activeCuePoint,
            });
            mediaEl.dispatchEvent(evt);
          }
        },
        {},
        track
      );

      resolve(track);
    });
  });
}
import { PlaybackEngine } from './types';

type MediaWithPDT = HTMLMediaElement & { getStartDate?: () => Date };

export function getStartDate(mediaEl: MediaWithPDT, hls: PlaybackEngine | undefined) {
  if (hls) {
    const playingDate = hls.playingDate;

    if (playingDate != null) {
      // If the video is very long and the currentTime will transition day boundaries,
      // this may end up not being accurate
      return new Date(playingDate.getTime() - mediaEl.currentTime * 1000);
    }
  }

  if (typeof mediaEl.getStartDate === 'function') {
    return mediaEl.getStartDate();
  }

  return new Date(NaN);
}

export function getCurrentPdt(mediaEl: MediaWithPDT, hls: PlaybackEngine | undefined) {
  if (hls && hls.playingDate) {
    return hls.playingDate;
  }

  if (typeof mediaEl.getStartDate === 'function') {
    const startDate = mediaEl.getStartDate();

    // If the video is very long and the currentTime will transition day boundaries,
    // this may end up not being accurate
    return new Date(startDate.getTime() + mediaEl.currentTime * 1000);
  }

  return new Date(NaN);
}
import type { LoaderResponse } from 'hls.js';
import {
  i18n,
  isJWTAudMismatch,
  isJWTAudMissing,
  isJWTExpired,
  isJWTSubMismatch,
  parseJwt,
  toPlaybackIdParts,
} from './util';
import { isKeyOf, MuxMediaPropsInternal, StreamTypes } from './types';
import type { MuxErrorCategoryValue } from './errors';
import { errorCategoryToTokenNameOrPrefix, MediaError, MuxErrorCategory, MuxErrorCode } from './errors';

export const MuxJWTAud = {
  VIDEO: 'v',
  // NOTE: These are not "built in" for mux-video/mux-audio (only mux-player) (CJP)
  THUMBNAIL: 't',
  STORYBOARD: 's',
  // GIF: 'g', // currently unused
  DRM: 'd',
} as const;

export const categoryToAud = (category: MuxErrorCategoryValue) => {
  if (category === MuxErrorCategory.VIDEO) return MuxJWTAud.VIDEO;
  if (category === MuxErrorCategory.DRM) return MuxJWTAud.DRM;
};

export const categoryToToken = (
  category: MuxErrorCategoryValue,
  muxMediaEl: Partial<Pick<MuxMediaPropsInternal, 'drmToken' | 'playbackToken' | 'tokens'>>
) => {
  const nameOrPrefix = errorCategoryToTokenNameOrPrefix(category);
  const tokenName = `${nameOrPrefix}Token` as const;
  if (muxMediaEl.tokens?.[nameOrPrefix]) return muxMediaEl.tokens?.[nameOrPrefix];
  return isKeyOf(tokenName, muxMediaEl) ? muxMediaEl[tokenName] : undefined;
};

export const getErrorFromResponse = (
  resp: Pick<Response, 'status' | 'url'> | Pick<LoaderResponse, 'code' | 'url'>,
  category: MuxErrorCategoryValue,
  muxMediaEl: Partial<
    Pick<MuxMediaPropsInternal, 'playbackId' | 'drmToken' | 'playbackToken' | 'tokens' | 'streamType'>
  >,
  fatal?: boolean,
  translate = false,
  offline = !globalThis.navigator?.onLine // NOTE: Passing this in for testing purposes
) => {
  if (offline) {
    const message = i18n(`Your device appears to be offline`, translate);
    const context = undefined;
    const mediaErrorCode = MediaError.MEDIA_ERR_NETWORK;
    // Being offline is not immediately a fatal error for playback.
    const mediaError = new MediaError(message, mediaErrorCode, false, context);
    mediaError.errorCategory = category;
    mediaError.muxCode = MuxErrorCode.NETWORK_OFFLINE;
    mediaError.data = resp;
    return mediaError;
  }
  const status = 'status' in resp ? resp.status : resp.code;
  const requestTime = Date.now();
  const mediaErrorCode = MediaError.MEDIA_ERR_NETWORK;
  // Not an error. WHAT ARE YOU EVEN DOING HERE?!?
  if (status === 200) return undefined;
  const tokenNamePrefix = errorCategoryToTokenNameOrPrefix(category);
  const token = categoryToToken(category, muxMediaEl);
  const expectedAud = categoryToAud(category);
  const [playbackId] = toPlaybackIdParts(muxMediaEl.playbackId ?? '');
  // NOTE: *should* have playback id when reaching here
  // if (!status) return MuxErrorCode.NETWORK_NO_STATUS;
  if (!status || !playbackId) return undefined;

  const jwtObj = parseJwt(token);
  // Make sure we didn't get here because of a malformed JWT and/or claim
  if (!!token && !jwtObj) {
    // 403 for DRM
    const message = i18n(`The {tokenNamePrefix}-token provided is invalid or malformed.`, translate).format({
      tokenNamePrefix,
    });
    const context = i18n(`Compact JWT string: {token}`, translate).format({
      token,
    });
    const mediaError = new MediaError(message, mediaErrorCode, true, context);
    mediaError.errorCategory = category;
    mediaError.muxCode = MuxErrorCode.NETWORK_TOKEN_MALFORMED;
    mediaError.data = resp;
    return mediaError;
  }

  if (status >= 500) {
    /**
     * @TODO We plausibly should have some basic retry logic for all other 500 status
     * cases (CJP)
     **/
    const mediaError = new MediaError('', mediaErrorCode, fatal ?? true);
    mediaError.errorCategory = category;
    mediaError.muxCode = MuxErrorCode.NETWORK_UNKNOWN_ERROR;
    /** @TODO Add error msg + context crud here (NOT YET DEFINED) (CJP) */
    return mediaError;
  }

  if (status === 403) {
    if (jwtObj) {
      if (isJWTExpired(jwtObj, requestTime)) {
        const dateOptions: any = {
          timeStyle: 'medium',
          dateStyle: 'medium',
        };
        // E.g. for DRM: "The videos secured drm-token has expired."
        // E.g. for Video: "The videos secured playback-token has expired."
        const message = i18n(`The videos secured {tokenNamePrefix}-token has expired.`, translate).format({
          tokenNamePrefix,
        });
        const context = i18n(`Expired at: {expiredDate}. Current time: {currentDate}.`, translate).format({
          expiredDate: new Intl.DateTimeFormat('en', dateOptions).format(jwtObj.exp ?? 0 * 1000),
          currentDate: new Intl.DateTimeFormat('en', dateOptions).format(requestTime),
        });
        const mediaError = new MediaError(message, mediaErrorCode, true, context);
        mediaError.errorCategory = category;
        mediaError.muxCode = MuxErrorCode.NETWORK_TOKEN_EXPIRED;
        mediaError.data = resp;
        return mediaError;
      }
      if (isJWTSubMismatch(jwtObj, playbackId)) {
        const message = i18n(
          `The videos playback ID does not match the one encoded in the {tokenNamePrefix}-token.`,
          translate
        ).format({
          tokenNamePrefix,
        });
        const context = i18n(
          `Specified playback ID: {playbackId} and the playback ID encoded in the {tokenNamePrefix}-token: {tokenPlaybackId}`,
          translate
        ).format({
          tokenNamePrefix,
          playbackId,
          tokenPlaybackId: jwtObj.sub,
        });
        const mediaError = new MediaError(message, mediaErrorCode, true, context);
        mediaError.errorCategory = category;
        mediaError.muxCode = MuxErrorCode.NETWORK_TOKEN_SUB_MISMATCH;
        mediaError.data = resp;
        return mediaError;
      }
      if (isJWTAudMissing(jwtObj, expectedAud)) {
        const message = i18n(`The {tokenNamePrefix}-token is formatted with incorrect information.`, translate).format({
          tokenNamePrefix,
        });
        const context = i18n(
          `The {tokenNamePrefix}-token has no aud value. aud value should be {expectedAud}.`,
          translate
        ).format({
          tokenNamePrefix,
          expectedAud,
        });
        const mediaError = new MediaError(message, mediaErrorCode, true, context);
        mediaError.errorCategory = category;
        mediaError.muxCode = MuxErrorCode.NETWORK_TOKEN_AUD_MISSING;
        mediaError.data = resp;
        return mediaError;
      }
      if (isJWTAudMismatch(jwtObj, expectedAud)) {
        const message = i18n(`The {tokenNamePrefix}-token is formatted with incorrect information.`, translate).format({
          tokenNamePrefix,
        });
        const context = i18n(
          `The {tokenNamePrefix}-token has an incorrect aud value: {aud}. aud value should be {expectedAud}.`,
          translate
        ).format({
          tokenNamePrefix,
          expectedAud,
          aud: jwtObj.aud,
        });
        const mediaError = new MediaError(message, mediaErrorCode, true, context);
        mediaError.errorCategory = category;
        mediaError.muxCode = MuxErrorCode.NETWORK_TOKEN_AUD_MISMATCH;
        mediaError.data = resp;
        return mediaError;
      }

      // NOTE: This *should* not happen for DRM, since the drm token
      // is currently used to detect whether or not DRM should
      // be setup at all. Including for exhaustiveness. (CJP)
    } else {
      const message = i18n(
        `Authorization error trying to access this {category} URL. If this is a signed URL, you might need to provide a {tokenNamePrefix}-token.`,
        translate
      ).format({
        tokenNamePrefix,
        category,
      });
      const context = i18n(`Specified playback ID: {playbackId}`, translate).format({ playbackId });
      const mediaError = new MediaError(message, mediaErrorCode, fatal ?? true, context);
      mediaError.errorCategory = category;
      mediaError.muxCode = MuxErrorCode.NETWORK_TOKEN_MISSING;
      mediaError.data = resp;
      return mediaError;
    }
  }

  if (status === 412) {
    const message = i18n(
      `This playback-id may belong to a live stream that is not currently active or an asset that is not ready.`,
      translate
    );
    const context = i18n(`Specified playback ID: {playbackId}`, translate).format({ playbackId });
    const mediaError = new MediaError(message, mediaErrorCode, fatal ?? true, context);
    mediaError.errorCategory = category;
    mediaError.muxCode = MuxErrorCode.NETWORK_NOT_READY;
    mediaError.streamType =
      muxMediaEl.streamType === StreamTypes.LIVE
        ? 'live'
        : muxMediaEl.streamType === StreamTypes.ON_DEMAND
          ? 'on-demand'
          : 'unknown';
    mediaError.data = resp;
    return mediaError;
  }

  /**
   * NOTE: When using a "structurally valid but non-existent" playback id for a DRM (license or app certificate) request, this will result in a 403 status.
   * However, since we will only currently make a DRM request after successfully loading the media, this case should not need
   * to be accounted for. If we ever eagerly fetch FPS app certs prior to or in parallel to media requests, we would potentially
   * want to account for that case (either by normalizing statuses, in our messaging for generic 403 above, or through more complex
   * solutions like waiting for the media response). (CJP)
   */
  if (status === 404) {
    // NOTE: This *should* not happen for DRM (only playback/media requests), since the URL should never be invalid if code
    // is correct. Aka if we end up here it's almost definitely a bug.
    const message = i18n(
      `This URL or playback-id does not exist. You may have used an Asset ID or an ID from a different resource.`,
      translate
    );
    const context = i18n(`Specified playback ID: {playbackId}`, translate).format({ playbackId });
    const mediaError = new MediaError(message, mediaErrorCode, fatal ?? true, context);
    mediaError.errorCategory = category;
    mediaError.muxCode = MuxErrorCode.NETWORK_NOT_FOUND;
    mediaError.data = resp;
    return mediaError;
  }

  /**
   * NOTE: Omitting a token for a DRM (license or app certificate) request results in a 400 status, unlike playback/media requests,
   * which are a 403 status (See above).
   * However, since we will only currently setup Mux Player for DRM if a drm token is provided, this case should not need
   * to be accounted for. If we ever change this, we would potentially
   * want to account for that case (either by normalizing statuses, in our messaging for generic 400 above, or through more complex
   * solutions like checking the category before deciding on error details here). (CJP)
   */
  if (status === 400) {
    const message = i18n(`The URL or playback-id was invalid. You may have used an invalid value as a playback-id.`);
    const context = i18n(`Specified playback ID: {playbackId}`, translate).format({ playbackId });
    const mediaError = new MediaError(message, mediaErrorCode, fatal ?? true, context);
    mediaError.errorCategory = category;
    mediaError.muxCode = MuxErrorCode.NETWORK_INVALID_URL;
    mediaError.data = resp;
    return mediaError;
  }

  const mediaError = new MediaError('', mediaErrorCode, fatal ?? true);
  mediaError.errorCategory = category;
  mediaError.muxCode = MuxErrorCode.NETWORK_UNKNOWN_ERROR;
  mediaError.data = resp;
  return mediaError;
};
import Hls from './hls';
import type { HlsInterface } from './hls';
import type { Level } from 'hls.js';
import type { MaxAutoResolutionValue } from './types';

// The hls.js commonJS module doesn't export CapLevelController, so get it from the default config.
const CapLevelController = Hls.DefaultConfig.capLevelController;

/**
 * Resolution pricing tiers based on total pixels (width * height)
 * Values align with Mux Video pricing tiers: https://www.mux.com/docs/pricing/video#resolution-based-pricing
 */
const RESOLUTION_PIXEL_LIMITS: Record<string, number> = {
  '720p': 921600, // Up to 921,600 pixels (1280x720)
  '1080p': 2073600, // Up to 2,073,600 pixels (1920x1080)
  '1440p': 4194304, // Up to 4,194,304 pixels (2560x1440)
  '2160p': 8294400, // Up to 8,294,400 pixels (3840x2160)
};

/**
 * Convert resolution string to maximum total pixels
 * Only accepts predefined tiers from the list
 */
function resolutionToMaxPixels(resolution: string): number | undefined {
  const normalized = resolution.toLowerCase().trim();

  return RESOLUTION_PIXEL_LIMITS[normalized];
}

/**
 * A custom HLS.js CapLevelController that behaves like the default one, except
 * it enforces a "minimum maximum" to avoid forced capping to lower quality at small sizes
 */
class MinCapLevelController extends CapLevelController {
  // Never cap below this level.
  static minMaxResolution = 720;
  private static maxAutoResolution = new WeakMap<HlsInterface, MaxAutoResolutionValue>();

  constructor(hls: HlsInterface) {
    super(hls);
  }

  static setMaxAutoResolution(hls: HlsInterface, maxAutoResolution: MaxAutoResolutionValue | undefined) {
    if (maxAutoResolution) {
      MinCapLevelController.maxAutoResolution.set(hls, maxAutoResolution);
    } else {
      MinCapLevelController.maxAutoResolution.delete(hls);
    }
  }

  private getMaxAutoResolution(): MaxAutoResolutionValue | undefined {
    // NOTE: hls is a TS-private member in CapLevelController. Should be TS-protected
    // @ts-ignore
    const hlsInstance = this.hls;
    return MinCapLevelController.maxAutoResolution.get(hlsInstance) ?? undefined;
  }

  get levels() {
    // NOTE: hls is a TS-private member in CapLevelController. Should be TS-protected (CJP)
    // @ts-ignore
    return (this.hls.levels ?? []) as Level[];
  }

  getValidLevels(capLevelIndex: number) {
    return this.levels.filter(
      // NOTE: isLevelAllowed is a TS-private member in CapLevelController. Should be TS-protected (CJP)
      // @ts-ignore
      (level, index) => this.isLevelAllowed(level) && index <= capLevelIndex
    );
  }

  /**
   * Get the maximum level capped to maxAutoResolution
   *
   * Selection logic (in order of priority):
   * 1. If there's an exact match for maxAutoResolution, use it
   * 2. If no exact match exists, always select the highest quality that doesn't exceed the cap
   *    (to prevent extra costs by going over the resolution limit)
   */
  private getMaxLevelCapped(capLevelIndex: number): number {
    const validLevels = this.getValidLevels(capLevelIndex);
    const maxAutoResolution = this.getMaxAutoResolution();

    if (!maxAutoResolution) {
      return super.getMaxLevel(capLevelIndex);
    }

    // Convert resolution string to maximum total pixels
    const maxPixels = resolutionToMaxPixels(maxAutoResolution);
    if (!maxPixels) {
      // Invalid resolution string, fallback to default behavior
      return super.getMaxLevel(capLevelIndex);
    }

    // Compare by total pixels (width * height) to match Mux Video pricing tiers
    // Find levels that don't exceed the cap (total pixels <= maxPixels)
    const levelsWithinCap = validLevels.filter((level) => {
      const totalPixels = level.width * level.height;
      return totalPixels <= maxPixels;
    });

    // Check if there's an exact match first (total pixels === maxPixels)
    const exactMatch = levelsWithinCap.findIndex((level) => {
      const totalPixels = level.width * level.height;
      return totalPixels === maxPixels;
    });

    if (exactMatch !== -1) {
      const exactLevel = levelsWithinCap[exactMatch];
      return validLevels.findIndex((level) => level === exactLevel);
    }

    // No exact match - always select the highest quality that doesn't exceed the cap
    if (levelsWithinCap.length === 0) {
      // No levels within cap, return the lowest level
      return 0;
    }
    // Return the highest quality within cap (last item, since levels are ordered from lowest to highest)
    const highestQualityWithinCap = levelsWithinCap[levelsWithinCap.length - 1];
    return validLevels.findIndex((level) => level === highestQualityWithinCap);
  }

  getMaxLevel(capLevelIndex: number) {
    if (this.getMaxAutoResolution() !== undefined) {
      return this.getMaxLevelCapped(capLevelIndex);
    }

    const baseMaxLevel = super.getMaxLevel(capLevelIndex);
    const validLevels = this.getValidLevels(capLevelIndex);

    // Default maxLevel selection ended up out of bounds to indicate e.g. no capping/no levels available (yet), so use it
    if (!validLevels[baseMaxLevel]) return baseMaxLevel;

    const baseMaxLevelResolution = Math.min(validLevels[baseMaxLevel].width, validLevels[baseMaxLevel].height);
    const preferredMinMaxResolution = MinCapLevelController.minMaxResolution;

    // Default maxLevel selection already meets our conditions, so use it
    if (baseMaxLevelResolution >= preferredMinMaxResolution) return baseMaxLevel;

    // Default maxLevel selection is below the preferred "min max", so find the lowest level
    // that is >= the preference. We can simply repurpose CapLevelController:getMaxLevelByMediaSize()
    // for this, "lying" about the element's size.
    // NOTE: Since CapLevelController:getMaxLevelByMediaSize() uses "max square size" under the hood
    // already, we don't need to duplicate that logic here.
    const maxLevel = CapLevelController.getMaxLevelByMediaSize(
      validLevels,
      preferredMinMaxResolution * (16 / 9),
      preferredMinMaxResolution
    );

    return maxLevel;
  }
}

export default MinCapLevelController;
